import styled from 'styled-components'

export const Container = styled.div`

    width: 100%;
    display: flex;
    flex-direction: column;
    background-color: #0fee52;

#outlined-multiline-static{
width: 100%;
}

.userBlockROW{
    display: flex;
    flex-direction: row;
    align-items: center;
}

.userBlockCOLLUM{
    margin: 5px;
    display: flex;
    flex-direction: column;

}

.infoPost{
margin-bottom: 0px;
margin-top: 0px;
padding: 0px;

}

.buttonPost{
    display: flex;
    justify-content: flex-end;
}

`
