import * as React from 'react';
import { Container } from './stylesPost'
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import AccountCircle from '@mui/icons-material/AccountCircle'


const Post = () => {
    return (
        <Container>
            <Box
                component="form"
                sx={{
                    '& .MuiTextField-root': { m: 1, width: '50%' },
                }}
                noValidate
                autoComplete="off"
            >

                <div className="userBlockCOLLUM">
                    <div className="userBlockROW">
                        <AccountCircle sx={{ color: 'action.active', mr: 1, my: 0.5 }} />
                        <h5>Nome do meliante</h5>
                        </div>
                    <h6>12:29</h6>
                </div>

                <TextField
                    id="outlined-multiline-static"
                    label="Publique o que quiser"
                    multiline
                    rows={4}
                />
                <LoadingButton
          size="small"
          onClick={handleClick}
          endIcon={<SendIcon />}
          loading={loading}
          loadingPosition="end"
          variant="contained"
        >
          Send
        </LoadingButton>
        <LoadingButton
          size="small"
          color="secondary"
          onClick={handleClick}
          loading={loading}
          loadingPosition="start"
          startIcon={<SaveIcon />}
          variant="contained"
        ></LoadingButton>

            </Box>
        </Container>
    )
}



export default Post
